import axios from 'axios';
import { IImage, IComment } from '../types';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

interface LoginCredentials {
  email: string;
  password: string;
}

interface SignupData {
  username: string;
  email: string;
  password: string;
}

interface ImageUploadData {
  file: File;
  caption: string;
}

export const login = async (credentials: LoginCredentials) => {
  const response = await axios.post(`${API_URL}/auth/login`, credentials);
  return response.data;
};

export const signup = async (userData: SignupData) => {
  const response = await axios.post(`${API_URL}/auth/signup`, userData);
  return response.data;
};

export const uploadImage = async (imageData: ImageUploadData) => {
  const response = await axios.post(`${API_URL}/images/upload`, imageData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
  return response.data;
};

export const fetchImages = async (): Promise<IImage[]> => {
  const response = await axios.get(`${API_URL}/images`);
  return response.data;
};

export const fetchImageDetails = async (imageId: string): Promise<IImage> => {
  const response = await axios.get(`${API_URL}/images/${imageId}`);
  return response.data;
};

export const likeImage = async (imageId: string): Promise<IImage> => {
  const response = await axios.post(`${API_URL}/images/${imageId}/like`);
  return response.data;
};

export const commentOnImage = async (imageId: string, commentData: { text: string }): Promise<IImage> => {
  const response = await axios.post(`${API_URL}/images/${imageId}/comments`, commentData);
  return response.data;
};
